var AOTcompile = require('./AOTcompile.js');
var template = require('./syntax/simple.js');
var Tmod = function (content,filename) {
	var t = AOTcompile(template);
	 modObject = t.AOTcompile(content,{ filename:filename,
		  type: 'amd',
		  escape: false,
		  runtime: 'template.js',
		  debug: true,
		  cache: false,
		  openTag: '{{',
		  closeTag: '}}' });
	 return modObject.code;
}
module.exports = Tmod;